import { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
    faCircleChevronLeft,
    faCircleChevronRight,
    faCircleXmark
} from '@fortawesome/free-solid-svg-icons'
import './Gallery.css'


const Gallery = () => {

    const galleryImages = [
        {
          img: "https://topinbangladesh.com/wp-content/uploads/2020/09/Top-10-College-in-Bangladesh.jpg"
        },
        {
          img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTbIZIJ7_PEZ1CxSe1oKoseMo6sM0Gq3Vjhyw&usqp=CAU"
        },
        {
          img: "data:image/jpeg;base64,"
        },
        {
          img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQU7VHRngP0exgjYaxuvj_pWTIvC211T0jNqQ&usqp=CAU"
        },
        {
          img: "data:image/jpeg;base64,"
        },
        {
          img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQIOoiTEMavNGU01ISPkDUz9SvWNljmBZuYDDctK1UQkfvm2qI6zBrTbxa2ZNSrPsRh8zE&usqp=CAU"
        },
        {
          img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnyRjrzI76RDXEypKEalYMKdMew7l8qdRw1Q&usqp=CAU"
        },
        {
          img: "https://topinbangladesh.com/wp-content/uploads/2020/09/Top-10-College-in-Bangladesh.jpg"
        },
        {
          img: "https://ndc.edu.bd/storage/app/uploads/public/63f/5f5/a4c/thumb_702_1662_710_0_0_crop.jpg"
        }
      ]

    const [slideNumber, setSlideNumber] = useState(0)
    const [openModal, setOpenModal] = useState(false)

    const handleOpenModal = (index) => {
        setSlideNumber(index)
        setOpenModal(true)
    }

    // Close Modal
    const handleCloseModal = () => {
        setOpenModal(false)
    }

    // Previous Image
    const prevSlide = () => {
        slideNumber === 0
            ? setSlideNumber(galleryImages.length - 1)
            : setSlideNumber(slideNumber - 1)
    }

    // Next Image  
    const nextSlide = () => {
        slideNumber + 1 === galleryImages.length
            ? setSlideNumber(0)
            : setSlideNumber(slideNumber + 1)
    }

    return (
        <div>
             <h1 className="text-3xl font-bold my-4 text-center">College Image Gallery</h1>
            {openModal &&
                <div className='sliderWrap'>
                    <FontAwesomeIcon icon={faCircleXmark} className='btnClose' onClick={handleCloseModal} />
                    <FontAwesomeIcon icon={faCircleChevronLeft} className='btnPrev' onClick={prevSlide} />
                    <FontAwesomeIcon icon={faCircleChevronRight} className='btnNext' onClick={nextSlide} />
                    <div className='fullScreenImage'>
                        <img src={galleryImages[slideNumber].img} alt='' />
                    </div>
                </div>
            }

            <div className='galleryWrap'>
                {
                    galleryImages && galleryImages.map((slide, index) => {
                        return (
                            <div
                                className='single'
                                key={index}
                                onClick={() => handleOpenModal(index)}
                            >
                                <img src={slide.img} alt='' />
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
};

export default Gallery;